package mc.ys.lesson2;

public class CodeMain {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 2~9단까지 출력하는 코드로 수정
		
		for(int i = 1; i <= 9; i++) {
			for(int j = 1; j <= 9; j++) {
				System.out.println(i + "*" + j + "=" + (i * j));
			}System.out.println();
			
		}

	}

}
